function arrayGrow( test )
{
  var got,expected;
  var array = [ 1,2,3,4,5 ];

  test.case = 'defaults';

  /* default call returns copy */

  got = _.arrayGrow( array );
  expected = array;
  test.identical( got, expected );

  test.case = 'increase size of array';

  /* without setting value */

